import React, { useEffect, useState } from "react";
import { useAuth0 } from "@auth0/auth0-react";

const Profile = () => {
  const { user, isAuthenticated, getTokenSilently } = useAuth0();
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const getProducts = async () => {
      try {
        const token = await getTokenSilently();  // getTokenSilently
        console.log(token)
        // Send a GET request to the server and add the signed in user's
        // access token in the Authorization header
        const response = await fetch("http://localhost:8080/products", {
          headers: {
            Authorization: `Bearer ${token}`,
            // Authorization: `Bearer eyJhbGciOiJSUzI1NiIsInR5cCI6IkpXVCIsImtpZCI6Ik9UazRRVGxHTnpNMlJqSTFOek13TVRFNVJVWkZPREZFTkRjM05rWTRNRGcyT1VVMFJqYzJOUSJ9..Fy1Y1iUN6r3pc_NFecb5FF6O-piH6ddp4wykaKVx8XKR76gCCSiBV6VzZVUrwFSEaW9dw2WmFHfhhcHMpMVKkPBYDeEUt-2UKzKuupiSXKqY5aoH0ZAxkqCiiNqoVeYA3eSeYHzyVMcxUbo8vnNjij1vuBR8QidqRdyHBkwcZqryN8bQG-Nj5QFRJfjWaQbVcwSFwrc47CG2e7noFn_DD1EUeQlacjLjrONdyHbAjocw4Ew4oBakTMlRCT8SHx7B_bLvkWE4eAm17FqL_hDYH1nWULtS2ET2xw9kX04w9rfFMMRV5zept9KCZzRbzMMNvkG5-S4jGNaHKFZuy3HlQA`,
          },
        });

        const responseData = await response.json();

        setProducts(responseData);
      } catch (error) {
        console.error(error);
      }
    };
    getProducts();
  }, []);

  return (
    isAuthenticated && (
      <div>
        <h2>{user.name}</h2>
        <p>{user.email}</p>
        <h3>From Go API</h3>
        <div>
          {products.map(function (product, index) {
              return (<div>{product.Name}</div>);
          })}
        </div>

      </div>
    )
  );
};

export default Profile;
